mapper.update:
update soil_quality
set acreage = #{acreage,jdbcType=DOUBLE},
  ph = #{ph,jdbcType=DOUBLE},
  wetness = #{wetness,jdbcType=DOUBLE},
  nutrient = #{nutrient,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, acreage, ph, wetness, nutrient
from soil_quality
where id = #{id,jdbcType=INTEGER}
