mapper.delete:
delete from staff_apply
where id in

  #{id}

update.foreach:
update staff_apply
set apply_status = 1
where id = #{id,jdbcType=INTEGER}

update staff_apply
set apply_status = 2
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, apply_type, date, staff_id, number, apply_status, note
from staff_apply
where id = #{id,jdbcType=INTEGER}
